SELECT t.* FROM t_goods t where t.u_id=#{id}
SELECT t.u_id, t.brand_id, t.goods_class_id, t.goods_name, t.`status`, t.price, t.image_id, t.carousel_id, t.`describe`,t.brand_name,t.class_name, IFNULL(sum(t3.quality),0) AS num,t.recommendStatus, t.cashBach, t.commission FROM (SELECT tgs.u_id, tgs.brand_id, tgs.goods_class_id, tgs.goods_name, tgs.`status`, tgs.price, tgs.image_id, tgs.carousel_id, tgs.`describe`,tb.name as brand_name,tgc.class_name as class_name, tgs.recommend_status AS recommendStatus, IFNULL(tgs.cashBach,0) AS cashBach , IFNULL(tgs.commission,0) AS commission FROM t_goods tgs,t_brand tb, t_goods_class tgc WHERE tgs.brand_id = tb.u_id AND tgs.goods_class_id = tgc.u_id AND left(tgs.goods_class_id, #{param.level})=#{param.goodsId}) t LEFT JOIN t_goods_specification t3 ON t.u_id = t3.goods_id GROUP BY t.u_id
SELECT COUNT(tgs.u_id) AS total FROM t_goods tgs, t_goods_class tgc WHERE tgs.goods_class_id = tgc.u_id AND left(tgs.goods_class_id, #{param.level})=#{param.goodsId}
SELECT tgs.u_id,tgs.brand_id, tgs.goods_class_id, tgs.goods_name, tgs.status, tgs.price, tgs.image_id, tgs.carousel_id,tgs.compressId, tgs.describe, tgs.num,tb.name as brand_name,tgc.class_name as class_name, IFNULL(tgs.cashBach,0) AS cashBach, IFNULL(tgs.commission,0) AS commission FROM t_goods tgs,t_brand tb,t_goods_class tgc WHERE tgs.u_id = #{id} AND tgs.brand_id = tb.u_id AND tgs.goods_class_id = tgc.u_id;
SELECT ts.goods_name AS goodsName, ts.u_id AS goodsId, tgc.class_name AS className FROM t_goods ts, t_goods_class tgc WHERE ts.goods_class_id = tgc.u_id AND ts.u_id NOT IN(SELECT tgs.goods_id FROM t_goods_shop tgs WHERE tgs.shop_id = #{param.shopId}) limit #{pageIndex},#{pageSize};
SELECT tg.goods_name AS goodsName, tfr.resource_id AS image FROM t_goods AS tg, t_file_ref AS tfr WHERE tg.u_id = #{goodsId} AND tg.image_id = tfr.ref_id LIMIT 1
SELECT tgs.u_id 'id', tgs.brand_id 'brandId', tgs.goods_class_id 'goodsClassId', tgs.goods_name 'goodsName', tgs.status 'status', tgs.price 'price', tgs.image_id 'imageId', tgs.carousel_id 'carouselId', tgs.num 'num',tgs.compressId, tgs.create_time 'createTime',t.sales, t2.isShow, IFNULL(tgs.cashBach,0) AS cashBach FROM t_goods tgs LEFT JOIN
(SELECT tgs.u_id 'id',COUNT(tod.u_id) 'sales' FROM t_goods tgs LEFT JOIN t_order_detail tod ON tgs.`status` != 0 AND tgs.u_id = tod.goods_id AND tod.`status` = 5
GROUP BY tgs.u_id) t ON tgs.u_id = t.id LEFT JOIN
(SELECT tg.u_id AS 'id', IFNULL(tvu.u_id,0) 'isShow' FROM t_goods tg LEFT JOIN t_video_user tvu ON tg.`status` != 0 AND tvu.`status` != 99 AND tg.u_id = tvu.goods_id GROUP BY tg.u_id) t2 ON t2.id = tgs.u_id WHERE tgs.`status` != 0
AND tgs.goods_class_id LIKE #{pattern}
AND tgs.brand_id = #{param.brandId}
AND tgs.price > #{param.startPrice}
AND tgs.price > #{param.startPrice} AND tgs.price < #{param.endPrice}
AND tgs.goods_name LIKE #{pattern1}
ORDER BY tgs.create_time DESC
ORDER BY sales DESC
ORDER BY sales
ORDER BY tgs.price DESC
ORDER BY tgs.price
limit #{pageIndex},#{pageSize};
