SELECT tgs.u_id 'id', tgs.goods_name 'goodsName', tgs.price 'price', tgs.image_id 'imageId', tgs.carousel_id 'carouselId',tgs.cashBach, T.create_time 'createTime',tgs.compressId, COUNT(tod.u_id) 'sales' FROM t_goods tgs,(SELECT tgs.u_id,tvu.create_time FROM t_video_user tvu LEFT JOIN t_goods tgs ON tvu.goods_id = tgs.u_id AND tvu.`status` != 99 GROUP BY tgs.u_id) T LEFT JOIN t_order_detail tod ON T.u_id = tod.goods_id AND tod.`status` = 5 WHERE tgs.u_id = T.u_id AND tgs.`status` != 0
AND tgs.goods_class_id LIKE #{pattern}
AND tgs.brand_id = #{param.brandId}
AND tgs.price > #{param.startPrice}
AND tgs.price > #{param.startPrice} AND tgs.price < #{param.endPrice}
AND tgs.goods_name LIKE #{pattern1}
GROUP BY T.u_id ORDER BY T.create_time DESC limit #{pageIndex},#{pageSize};
