SELECT tgs.u_id 'uId',tgs.goods_name 'goodsName',tgs.carousel_id 'carouselId',tgs.price 'price',tgs.num 'num',tgs.describe 'describe',IFNULL(tgs.cashBach,0) AS cashBach, tgs.status 'status',tgs.create_time 'createTime',tgs.commission,COUNT(tod.u_id) 'sales' FROM t_goods tgs, t_order_detail tod WHERE tgs.u_id = tod.goods_id AND tgs.u_id = #{uId} AND tod.`status` = 5
SELECT price FROM t_goods WHERE u_id = #{uId}
SELECT tgs.goods_name AS goodsName, tgs.price, tgs.image_id 'imageId', IFNULL(tgs.cashBach,0) AS cashBach, t.goods_id AS goodsId, t.sales, IFNULL( tgs.commission, 0) AS commission FROM t_goods tgs, (SELECT tgsh.goods_id,COUNT(tod.u_id) 'sales' FROM t_goods_shop tgsh LEFT JOIN t_order_detail tod ON tgsh.goods_id = tod.goods_id AND tod.`status` = 5 AND tgsh.shop_id = #{param.shopId} GROUP BY tgsh.goods_id) t WHERE tgs.u_id = t.goods_id
AND tgs.goods_class_id = #{param.classId}
AND tgs.brand_id = #{param.brandId}
AND tgs.goods_name LIKE #{goodsName}
limit #{pageIndex},#{pageSize}
